import { Link } from 'react-router-dom'
import { useState } from 'react'
import axios from 'axios';
import Button from './common/Button';


const Login = () => {
  const [user, setUser] = useState({});
  /* 
      const handleLogin = async () => {
        console.log(user)
        await axios.post(`http://localhost:5000/user`, user)

      }
  */
  return (
    <div className='flex justify-center content-center outline h-screen'>
      <div className='bg-blue-400 bg-opacity-25 rounded-md h-4 outline outline-slate-700 p-3 mb-2'>

        <input
          type='text'
          placeholder='Enter username'
          onChange={(e) => setUser(e.target.value)}
        />

        <Link className='cursor-pointer' to='/Home' state={{ from: { user } }}>
          <Button text='Login' />
        </Link>
      </div>
    </div>
  );
}

export default Login;
