    return (

        <div className="fixed flex-col items-center top-0 left-0 w-56 h-screen bg-blue-500 shadow-lg p-3 md:hidden ">
            <h1 className='flex h-1/6 font-bold text-white text-5xl text-shadow-lg mt-3' >TRS</h1>
            <Option text='Monthly Reports'/>
        </div>
    )
}



const Option = ({text}) => {
    return (
        <div className='bg-yellow-600 outline outline-white h-16 w-full block items-center text-white text-center p-5 rounded-2xl hover:bg-yellow-500 cursor-pointer'>
            {text}
        </div>
    )
}

export default Sidebar
