import { useState } from 'react'

const UpdateEntry = ({ onUpdateEntry, entry }) => {
  const [code, setCode] = useState(entry.code)
  const [time, setTime] = useState(entry.time)
  const [description, setDescription] = useState(entry.description)
  const [date, setDate] = useState(entry.date)
  const [id, setId] = useState(entry.id)

  const onSubmit = (e) => {
    e.preventDefault()

    onUpdateEntry({ code, time, description, date, id })
    setCode('')
    setTime('')
    setDate('')
    setId('')
    setDescription('')

  }

  return (
    <form className=' m-3 bg-blue-400 bg-opacity-25 rounded-md outline outline-blue-500 p-3' onSubmit={onSubmit}>
      <div className='grid grid-cols-3 gap-3'>
      <div className='col-span-2 col-start-1 row-start-1 row-end-1 grid grid-rows-3 w-1/2 gap-4'>
        <div>
          <label> <span className='font-bold'>Code: </span> {code} </label>
        </div>
        <div>
          <input
            type='text'
            placeholder='Enter Time'
            value={time}
            onChange={(e) => setTime(e.target.value)}
            className='mt-1'
          />
        </div>
        <div>
          <input
            type='text'
            placeholder='Enter Description'
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />

        </div>
      </div>
      <div className=' relative col-start-3 col-end-3 row-start-1 row-end-1 col-span-1'>
        <input className='bg-yellow-500 p-3 ml-3 rounded mt-1 outline outline-white cursor-pointer hover:bg-yellow-600  ' type='submit' value='Update' />
      </div>
      </div>
    </form>
  )
}

export default UpdateEntry
