import { useState } from 'react'
import Select from 'react-select'

const AddEntry = ({ onAddEntry }) => {
  const [code, setCode] = useState('aa')
  const [time, setTime] = useState('')
  const [description, setDescription] = useState('')

  const opts = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' }
  ]


  const onSubmit = (e) => {
    e.preventDefault()



    if (!code) {
      alert('Please enter a code')
      return
    }

    onAddEntry({ code, time, description })

    setCode('')
    setTime('')
    setDescription('')
  }

  return (
    <form className='m-3 bg-blue-400 bg-opacity-25 rounded-md outline outline-blue-500 p-3' onSubmit={onSubmit}>
      <div className='grid grid-cols-2 grid-rows-4 gap-4'>

        <Select
          options={opts}
          onChange={(e) => setCode(e.value)}
          placeholder='Select Code'
        />

        <input
          type='text'
          placeholder='Enter Time'
          value={time}
          onChange={(e) => setTime(e.target.value)}

        />


        <input
          type='text'
          placeholder='Enter Description'
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
          <input className='row-span-8  bg-yellow-500 p-3 ml-7 rounded mt-1 outline outline-white cursor-pointer hover:bg-yellow-600 '
        type='submit'
        value='Submit' />


      </div>



    </form>
  )
}

export default AddEntry
